import ReactAnime from 'react-animejs'
import { useState, useEffect } from 'react';

function App() {
  const [num, setNum] = useState(5)
  const { Anime } = ReactAnime;

  const vw = Math.max(document.documentElement.clientWidth, window.innerWidth);
  const vh= Math.max(document.documentElement.clientHeight, window.innerHeight);

  const randomRadius = () => {
    console.log(Math.random() * 0.7 + 0.6);
  }

  const getRandomX = () => {
    console.log(Math.floor(Math.random() * Math.floor(vw)).toString()); 
  }

  const getRandomY = () => {
    return Math.floor(Math.random() * Math.floor(vh)).toString();
  }

  useEffect(() => {
    randomRadius();
    getRandomX();
    getRandomY();
  }, [])

  return (
    <div className="App">
      <Anime
        initial={[
          {
            targets: ".sky",
            opacity: [
              {
                duration: 700,
                value: "0"
              },
              {
                duration: 700,
                value: "1"
              }
            ],
            easing: "linear",
            loop: true,
          }
        ]}
      >
        <svg className="sky">
          <circle
            cx="50"
            cy="50"
            r="40"
            stroke="green"
            strokeWidth="4"
            fill="yellow"
          />
        </svg>
      </Anime>
    </div>
  );
}

export default App;
