import { makeStyles } from "@material-ui/core";

export const useStyles = makeStyles((theme) => ({
    root: {
        borderBottom: '1px solid var(--border)',
        color: 'white',
    },
    wrapper: {
        display: 'flex',
        flexWrap: 'wrap',
        textAlign: 'center',
        justifyContent: 'center',
    },
    title: {
        flexGrow: '1',
        textTransform: 'uppercase',
        fontSize: '35px',
        padding: '20px',
        ...theme.typography.antonio,
        textAlign: 'left',
        '@media (max-width: 1283px)': {
            textAlign: 'center',
        }
    },
    link: {
        ...theme.typography.spartan,
        textTransform: 'uppercase',
        fontWeight: '600',
        fontSize: '15px',
        display: 'inline-block',
        padding: '35px 20px',
        margin: '0 12px',
        color: 'var(--link)',
        '&::after': {
            content: '""',
            display: "block",
            margin: "auto",
            height: "3px",
            width: "0px",
            background: "transparent",
            transition: "width .5s ease, background-color .5s ease"
        },
        '&:hover::after': {
            width: "100%",
            background: "blue"
        }
        // '&:hover': {           
        //     padding: '32px 20px', 
        //     borderTop: ({borderColor}) => borderColor,
        //     color: 'inherit',
        //     transition: 'border-width 0.6s linear'
        // },
        // '@media (max-width: 1283px)': {
        //     '&:hover': {     
        //         borderTop: 'none',      
        //         borderBottom: '3px solid var(--border)',
        //     }
        // }
    }
}));
