import { makeStyles } from "@material-ui/core";

export const useStyles = makeStyles((theme) => ({
    root: {
        color: 'white',
    },
    images:{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        margin: "auto",
        marginTop:'150px',
    },
    img: {
        '@media (max-width: 700px)': {
            maxWidth: '250px',
        },
    },
    title: {
        ...theme.typography.antonio,
        textTransform: 'uppercase',
        marginTop: '100px',
        cursor: 'pointer'
    },
    body: {
        ...theme.typography.spartan,
        lineHeight: '1.8'
    },
    wikipedia: {
        cursor: 'pointer',
        textDecoration: 'underline',
        color: '#747d8c',
        '&:hover': {
            color: 'inherit'
        }
    },
    btn: {
        display: 'block',
        border: '1px solid var(--link)'
    },
    number: {
        color: 'var(--link)',
        padding: '20px',
        ...theme.typography.spartan,
    },
    launch: {
        marginTop: '10px',
        paddingTop: '8px',
    }
}));
