import { useStyles } from "./styles";
import { Grid, Container, Typography, Link } from '@material-ui/core';
import { useParams } from 'react-router-dom';
import { PlanetContext } from '../../contexts/PlanetContext';
import { useContext } from 'react';
import { useEffect } from 'react';
import axios from "axios";
import LaunchIcon from '@material-ui/icons/Launch';

function Main() {
    const classes = useStyles();
    const { name } = useParams();
    const { planet, setPlanet } = useContext(PlanetContext);

    useEffect(() => {
        const fetchPlanet = async () => {
            try {
                const { data } = await axios.get(`http://localhost:8080/planets/?name=${name}`);
                setPlanet(data);

            }
            catch (err) {
                console.error(err);
            }
        }
        fetchPlanet();
    }, [name, setPlanet]);

    const color = planet.map(el => el.color);
    console.log(color[0]);
    return (
        <Container className={classes.root}>
            <Grid container >
                <Grid item md={12} lg={8} className={classes.images}>
                    <img
                        src={planet.map(el => el.images.planet)}
                        className={classes.img}
                        alt="planet"
                    />
                </Grid>
                <Grid item md={12} lg={4} className={classes.content}>
                    <Typography
                        variant="h1"
                        component="h2"
                        className={classes.title}
                        gutterBottom
                    >
                        {planet.map(el => el.name)}
                    </Typography>
                    <Typography
                        className={classes.body}
                    >
                        {planet.map(el => el.overview.content)}
                    </Typography>
                    <Typography variant="h6">
                        <Link
                            href="#"
                            underline="none"
                            color='inherit'
                        >
                            Source:
                            <span className={classes.wikipedia}>
                                Wikipedia
                                <LaunchIcon className={classes.launch} />
                            </span>
                        </Link>
                    </Typography>
                    <Typography variant="h6">
                        <Link
                            underline="none"
                            color='inherit'
                            className={classes.btn}
                        >
                            <span className={classes.number}>
                                01
                            </span>overview
                        </Link>
                        <Link
                            underline="none"
                            color='inherit'
                            className={classes.btn}
                        >
                            <span className={classes.number}>
                                02
                            </span>infernal structure
                        </Link>
                        <Link
                            underline="none"
                            color='inherit'
                            className={classes.btn}
                        >
                            <span className={classes.number}>
                                03
                            </span>surface geology
                        </Link>
                    </Typography>
                </Grid>
            </Grid>
        </Container>
    )
}

export default Main
