import { useStyles } from "./styles";
import { Grid, Container, Typography, Link } from '@material-ui/core';
import { useParams } from 'react-router-dom';
import { PlanetContext } from '../../contexts/PlanetContext';
import { useContext } from 'react';
import { useEffect } from 'react';
import axios from "axios";

function Main() {
    const classes = useStyles();
    const { name } = useParams();
    const { planet, setPlanet } = useContext(PlanetContext);

    useEffect(() => {
        const fetchPlanet = async () => {
            try {
                const { data } = await axios.get(`http://localhost:8080/planets/?name=${name}`);
                setPlanet(data);

            }
            catch (err) {
                console.error(err);
            }
        }
        fetchPlanet();
    }, [name, setPlanet]);

    return (
        <Container>
            <Grid container>
                <Grid item sm={12} md={8}>
                    Image
                </Grid>
                <Grid item sm={12} md={4}>
                    <Typography
                        variant="h1"
                        component="h2"
                        className={classes.title}
                        gutterBottom
                    >
                        {planet.map(el => el.name)}
                    </Typography>
                    <Typography
                        className={classes.body}
                    >
                        {planet.map(el => el.overview.content)}
                    </Typography>
                    <Typography>
                        Source: 
                        <Link
                            href="#"
                            underline="none"
                        >
                            <span className={classes.link}> Wikipedia</span>
                        </Link>
                    </Typography>
                </Grid>
            </Grid>
        </Container>
    )
}

export default Main
